mapper.select:


SELECT bno, title, content, userid, regdate, updatedate, replyCnt, cnt, gs_type
FROM
(
    SELECT /*+INDEX_DESC(gs_board pk_gs_board)*/ 
    ROWNUM rn, bno, title, content, userid, regdate, updatedate, replyCnt, cnt, gs_type
    FROM gs_board 
    WHERE 1=1



    AND gs_type = #{gs_type}
)
WHERE ROWNUM <= #{pageNum} * #{amount}
  AND rn > (#{pageNum} - 1) * #{amount}



SELECT COUNT(*)
FROM gs_board
WHERE gs_type = #{gs_type}

mapper.update:
update gs_board
set cnt = cnt + 1
where bno = #{bno}

update gs_board
set recommend_count = recommend_count + #{count}
where bno = #{bno}

update gs_board
set recommend_count = recommend_count - #{count}
where bno = #{bno}

mapper.select:
select recommend_count
from gs_board
where bno = #{bno}

mapper.delete:
DELETE FROM gs_like
WHERE user_id = #{username}
AND board_id = #{bno}

mapper.select:
SELECT COUNT(*) FROM gs_like
WHERE board_id = #{bno}
AND user_id = #{username}
AND like_status = 1
