mapper.select:

    		SELECT bno, title, content, userid, regdate, updatedate, replyCnt, cnt
    		FROM 
    		(
        		SELECT /*+INDEX_DESC(gs_board pk_gs_board)*/
        		rownum rn, bno, title, content, userid, regdate, updatedate, replyCnt, cnt
        		FROM gs_board
        		WHERE userid = #{userid, jdbcType=VARCHAR}
    			and rownum <= #{cri.pageNum} * #{cri.amount} 
			)
			where rn > (#{cri.pageNum} -1) * #{cri.amount}

mapper.update:
UPDATE gs_member
SET userpw = #{userpw}, username = #{username,jdbcType=VARCHAR}
WHERE userid = #{userid}
